re class=\"wp-block-image\"><img src=\"http://bdshungthinh365.com/wp-content/uploads/2019/12/Screen-Shot-2019-12-09-at-17.15.16-1024x574.png\" alt=\"\" class=\"wp-image-1137\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":1138} -->\n<figure class=\"wp-block-image\"><img src=\"http://bdshungthinh365.com/wp-content/uploads/2019/12/Screen-Shot-2019-12-09-at-17.15.29-1024x569.png\" alt=\"\" class=\"wp-image-1138\"/></figure>\n<!-- /wp:image -->', 'CĂN HỘ CAO CẤP GRAND CENTER QUY NHON', '', 'inherit', 'closed', 'closed', '', '1124-revision-v1', '', '', '2019-12-09 17:34:41', '2019-12-09 10:34:41', '', 1124, 'http://bdshungthinh365.com/2019/12/09/1124-revision-v1/', 0, 'revision', '', 0),
